import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CountryChart from './CountryChart';

function About() {
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Replace 'API_URL' with the actual URL of the API endpoint for a specific country.
    const apiUrl = 'http://localhost:8000/api/countrydata/1'; // Assuming 1 is the country ID.

    axios.get(apiUrl)
      .then((response) => {
        setData(response.data);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setIsLoading(false);
      });
  }, []);

  return (
    <div className="About">
      <h1>Country Data Chart</h1>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <CountryChart data={data} />
      )}
    </div>
  );
}

export default About;
