import React, { useState } from 'react';
import axios from 'axios';
import CountryChart from './CountryChart'; // Import the CountryChart component

function CountryDataSearch() {
    const [countryName, setCountryName] = useState('');
    const [data, setData] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const handleSearch = () => {
        setIsLoading(true);

        // Make an API request to get data for the specified country name
        axios.get(`http://localhost:8000/api/countrydata/${countryName}/`)
            .then((response) => {
                setData(response.data);
                setIsLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
                setIsLoading(false);
            });
    };

    return (
        <div>
            <h1>Country Data Search</h1>
            <input
                type="text"
                placeholder="Enter Country Name"
                value={countryName}
                onChange={(e) => setCountryName(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>

            {isLoading ? (
                <div>Loading...</div>
            ) : (
                // Render the CountryChart component and pass the data as a prop
                <CountryChart data={data} />
            )}
        </div>
    );
}

export default CountryDataSearch;
